import logo from './logo.svg';
import React, { useState } from "react";
import './App.css';

function App() {
  const [inputValue, setInputValue] = useState("");
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState(null);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleButtonClick = () => {
    setLoading(true);
    console.log(
    JSON.stringify({
            "data" : {
                "text_inputs": inputValue,
                "max_length": 50,
                "num_return_sequences": 3,
                "top_k": 50,
                "top_p": 0.95,
                "do_sample": true
            }
        })
    ); 
    fetch("https://localhost:8000/generate_response", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body : JSON.stringify({
        "data" : {
            "text_inputs": inputValue,
            "max_length": 50,
            "num_return_sequences": 3,
            "top_k": 50,
            "top_p": 0.95,
            "do_sample": true
        }
    })
      // body: JSON.stringify({ text: inputValue }),
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setResponse(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  };

  return (
    <div className="container">
      <div className="upper">
        <input
          type="text"
          className="input"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Enter text here..."
        />
        <button className="button" onClick={handleButtonClick}>
          Send
        </button>
      </div>
      <div className="lower">
        {loading && <div className="spinner"></div>}
        {response && (
          <>
            <div className="summary-tile">
              <div className="tile-title">Summary</div>
              <div className="tile-text">{response.summary}</div>
            </div>
            <div className="stakeholders-tile">
              <div className="tile-title">Stakeholders</div>
              {response.stakeholders.map((stakeholder, index) => (
                <div key={index} className="tile-text">
                  {stakeholder.stakeholder}
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
